package Operators;

public class Logical_Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean a=true,b=false;
		
		//'&&'--> AND Operator
		System.out.println("\nBoolean Values of a and b are true : "+(a && b));
		
		//'||'--> OR Operator
		System.out.println("\nBoolean Value of a or b is true : "+(a || b));
				
		//'!'--> NOT Operator
		System.out.println("\nBoolean Value of a is changed to : "+(!a));	
		
		
	}

}
